from pip import SourceDistribution
from pip import WheelDistribution

from pip import MYPY_CHECK_RUNNING

if MYPY_CHECK_RUNNING:
    from pip import AbstractDistribution
    from pip import InstallRequirement


def make_distribution_for_install_requirement(install_req):
    # type: (InstallRequirement) -> AbstractDistribution
    """Returns a Distribution for the given InstallRequirement
    """
    # If it's not an editable, is a wheel, it's a WheelDistribution
    if install_req.editable:
        return SourceDistribution(install_req)

    if install_req.link and install_req.is_wheel:
        return WheelDistribution(install_req)

    # Otherwise, a SourceDistribution
    return SourceDistribution(install_req)
